f struct list_entry list_entry_t;\n\nstatic inline void list_init(list_entry_t *elm) __attribute__((always_inline));\nstatic inline void list_add(list_entry_t *listelm, list_entry_t *elm) __attribute__((always_inline));\nstatic inline void list_add_before(list_entry_t *listelm, list_entry_t *elm) __attribute__((always_inline));\nstatic inline void list_add_after(list_entry_t *listelm, list_entry_t *elm) __attribute__((always_inline));\nstatic inline void list_del(list_entry_t *listelm) __attribute__((always_inline));\nstatic inline void list_del_init(list_entry_t *listelm) __attribute__((always_inline));\nstatic inline bool list_empty(list_entry_t *list) __attribute__((always_inline));\nstatic inline list_entry_t *list_next(list_entry_t *listelm) __attribute__((always_inline));\nstatic inline list_entry_t *list_prev(list_entry_t *listelm) __attribute__((always_inline));\n\nstatic inline void __list_add(list_entry_t *elm, list_entry_t *prev, list_entry_t *next) __attribute__((always_inline));\nstatic inline void __list_del(list_entry_t *prev, list_entry_t *next) __attribute__((always_inline));\n\n/* *\n * list_init - initialize a new entry\n * @elm:        new entry to be initialized\n * */\nstatic inline void\nlist_init(list_entry_t *elm) {\n    elm-&gt;prev = elm-&gt;next = elm;\n}\n\n/* *\n * list_add - add a new entry\n * @listelm:    list head to add after\n * @elm:        new entry to be added\n *\n * Insert the new element @elm *after* the element @listelm which\n * is already in the list.\n * */\nstatic inline void\nlist_add(list_entry_t *listelm, list_entry_t *elm) {\n    list_add_after(listelm, elm);\n}\n\n/* *\n * list_add_before - add a new entry\n * @listelm:    list head to add before\n * @elm:        new entry to be added\n *\n * Insert the new element @elm *before* the element @listelm which\n * is already in the list.\n * */\nstatic inline void\nlist_add_before(list_entry_t *listelm, list_entry_t *elm) {\n    __list_add(elm, listelm-&gt;prev, listelm);\n}\n\n/* *\n * list_add_after - add a new entry\n * @listelm:    list head to add after\n * @elm:        new entry to be added\n *\n * Insert the new element @elm *after* the element @listelm which\n * is already in the list.\n * */\nstatic inline void\nlist_add_after(list_entry_t *listelm, list_entry_t *elm) {\n    __list_add(elm, listelm, listelm-&gt;next);\n}\n\n/* *\n * list_del - deletes entry from list\n * @listelm:    the element to delete from the list\n *\n * Note: list_empty() on @listelm does not return true after this, the entry is\n * in an undefined state.\n * */\nstatic inline void\nlist_del(list_entry_t *listelm) {\n    __list_del(listelm-&gt;prev, listelm-&gt;next);\n}\n\n/* *\n * list_del_init - deletes entry from list and reinitialize it.\n * @listelm:    the element to delete from the list.\n *\n * Note: list_empty() on @listelm returns true after this.\n * */\nstatic inline void\nlist_del_init(list_entry_t *listelm) {\n    list_del(listelm);\n    list_init(listelm);\n}\n\n/* *\n * list_empty - tests whether a list is empty\n * @list:       the list to test.\n * */\nstatic inline bool\nlist_empty(list_entry_t *list) {\n    return list-&gt;next == list;\n}\n\n/* *\n * list_next - get the next entry\n * @listelm:    the list head\n **/\nstatic inline list_entry_t *\nlist_next(list_entry_t *listelm) {\n    return listelm-&gt;next;\n}\n\n/* *\n * list_prev - get the previous entry\n * @listelm:    the list head\n **/\nstatic inline list_entry_t *\nlist_prev(list_entry_t *listelm) {\n    return listelm-&gt;prev;\n}\n\n/* *\n * Insert a new entry between two known consecutive entries.\n *\n * This is only for internal list manipulation where we know\n * the prev/next entries already!\n * */\nstatic inline void\n__list_add(list_entry_t *elm, list_entry_t *prev, list_entry_t *next) {\n    prev-&gt;next = next-&gt;prev = elm;\n    elm-&gt;next = next;\n    elm-&gt;prev = prev;\n}\n\n/* *\n * Delete a list entry by making the prev/next entries point to each other.\n *\n * This is only for internal list manipulation where we know\n * the prev/next entries already!\n * */\nstatic inline void\n__list_del(list_entry_t *prev, list_entry_t *next) {\n    prev-&gt;next = next;\n    next-&gt;prev = prev;\n}\n\n#endif /* !__ASSEMBLER__ */\n\n#endif /* !__LIBS_LIST_H__ */\n\n\n============= Swap_fifo.c (kern\\mm)=============\n\n#include &lt;defs.h&gt;\n#include &lt;x86.h&gt;\n#include &lt;stdio.h&gt;\n#include &lt;string.h&gt;\n#include &lt;swap.h&gt;\n#include &lt;swap_fifo.h&gt;\n#include &lt;list.h&gt;\n\n/* [wikipedia]The simplest Page Replacement Algorithm(PRA) is a FIFO algorithm. \n* (1) Prepare: In order to implement FIFO PRA, we should manage all swappable pages, so we can\n *              link these pages into pra_list_head according the time order. At first you should\n *              be familiar to the struct list in list.h. struct list is a simple doubly linked list\n *              implementation. You should know howto USE: list_init, list_add(list_add_after),\n *              list_add_before, list_del, list_next, list_prev. Another tricky method is to transform\n *              a general list struct to a special struct (such as struct page). You can find some MACRO:\n *              le2page (in memlayout.h), (in future labs: le2vma (in vmm.h), le2proc (in proc.h),etc.\n */\n\nlist_entry_t pra_list_head;\n/*\n * (2) _fifo_init_mm: init pra_list_head and let  mm-&gt;sm_priv point to the addr of pra_list_head.\n *              Now, From the memory control struct mm_struct, we can access FIFO PRA\n */\nstatic int\n_fifo_init_mm(struct mm_struct *mm)\n{     \n     list_init(&amp;pra_list_head);\n     mm-&gt;sm_priv = &amp;pra_list_head;\n     //cprintf(&quot; mm-&gt;sm_priv %x in fifo_init_mm\\n&quot;,mm-&gt;sm_priv);\n     return 0;\n}\n/*\n * (3)_fifo_map_swappable: According FIFO PRA, we should link the most recent arrival page at the back of pra_list_head qeueue\n */\nstatic int\n_fifo_map_swappable(struct mm_