6_t tf_padding4;\n} __attribute__((packed));\n...\n---------------------------------\n\nkern/trap/trap.c\n---------------------------------\n...\nstatic int\npgfault_handler(struct trapframe *tf) {\n    extern struct mm_struct *check_mm_struct;\n\n}\nprint_pgfault(tf);\n    if (check_mm_struct != NULL) {\n        return do_pgfault(check_mm_struct, tf-&gt;tf_err, rcr2());\n    }\n    panic(&quot;unhandled page fault.\\n&quot;);\n}\n\nstatic void\ntrap_dispatch(struct trapframe *tf) {\nchar c;\nint ret;\nswitch ( --YOUR CODE 1-- ) {\n     .YOUR..\ncase T_PGFLT:\nif ( --YOUR CODE 2-- ) != 0) {\nprint_trapframe(trapf);\nif (current == NULL) {\npanic(&quot;handle pgfault failed. %e\\n&quot;,              ret);\n}\nelse { ... }\n}\nbreak;\n  ...\n}\n\nvoid\ntrap(struct trapframe *tf) {\n    // dispatch based on what type of trap occurred\n    trap_dispatch(tf);\n}\n...\n// do_pgfault - interrupt handler to process the page fault execption\nint\ndo_pgfault(struct mm_struct *mm, uint32_t error_code, uintptr_t addr) {\n    int ret = -E_INVAL;\n    struct vma_struct *vma = find_vma(mm, addr);\n    if (vma == NULL || vma-&gt;vm_start &gt; addr) {\n        goto failed;\n    }\n\n    switch (error_code &amp; 3) {\n    default:\n            /* default is 3: write, present */\n    case 2: /* write, not present */\n        if (!(vma-&gt;vm_flags &amp; VM_WRITE)) {\n            goto failed;\n        }\n        break;\n    case 1: /* read, present */\n        goto failed;\n    case 0: /* read, not present */\n        if (!(vma-&gt;vm_flags &amp; (VM_READ | VM_EXEC))) {\n            goto failed;\n        }\n    }\n\n    uint32_t perm = PTE_U;\n    if (vma-&gt;vm_flags &amp; VM_WRITE) {\n        perm |= PTE_W;\n    }\n    addr = ROUNDDOWN(addr, PGSIZE);\n\n    ret = -E_NO_MEM;\n\n    if (pgdir_alloc_page(mm-&gt;pgdir, addr, perm) == 0) {\n        goto failed;\n    }\n    ret = 0;\n\nfailed:\n    return ret;\n}\n...\n---------------------------------\n\nPmm.h\n---------------------------------\n...\n//ppn is physical page number\nstatic inline ppn_t\npage2ppn(struct Page *page) {\n    return --YOUR CODE 3--;\n}\n//pa is physical address\nstatic inline uintptr_t\npage2pa(struct Page *page) {\n    return --YOUR CODE 4--;\n}\n...\n---------------------------------\npmm.c\n---------------------------------\n...\n// virtual address of physicall page array\nstruct Page *pages;\n// amount of physical memory (in pages)\nsize_t npage = 0;\n// virtual address of boot-time page directory\npde_t *boot_pgdir = NULL;\n……\n// pgdir_alloc_page - call alloc_page &amp; page_insert functions to \n//                  - allocate a page size memory &amp; setup an addr map\n//                  - pa&lt;-&gt;la with linear address la and the PDT pgdir\nstruct Page *\npgdir_alloc_page(pde_t *pgdir, uintptr_t la, uint32_t perm) {\n    struct Page *page = alloc_page();\n    if (page != NULL) {\n        if (page_insert(pgdir, page, la, perm) != 0) {\n            free_page(page);\n            return NULL;\n        }\n    }\n    return page;\n}\n...\n\n//page_insert - build the map of phy addr of an Page with the linear addr la\n// paramemters:\n//  pgdir: the kernel virtual base address of PDT\n//  page:  the Page which need to map\n//  la:    the linear address need to map\n//  perm:  the permission of this Page which is setted in related pte\n// return value: always 0\n//note: PT is changed, so the TLB need to be invalidate \nint\npage_insert(pde_t *pgdir, struct Page *page, uintptr_t la, uint32_t perm) {\n    pte_t *ptep = get_pte(pgdir, la, 1);\n    if (ptep == NULL) {\n        return -E_NO_MEM;\n    }\n    page_ref_inc(page);\n    if (*ptep &amp; PTE_P) {\n        struct Page *p = pte2page(*ptep);\n        if (p == page) {\n            page_ref_dec(page);\n        }\n        else {\n            page_remove_pte(pgdir, la, ptep);\n        }\n    }\n    *ptep = --YOUR CODE 5--\n    tlb_invalidate(pgdir, la);\n    return 0;\n}\n---------------------------------\n&lt;/code&gt;&lt;/pre&gt;',0,'','','',0,'&lt;p&gt;Code1：  tf-&gt;tf_trapno\nCode 2:   ret = pgfault_handler(tf)\nCode 3:   page - pages;\nCode 4:   page2ppn(page) &lt;&lt; PGSHIFT\nCode 5:   page2pa(page) | PTE_P | perm;\n---------------------------------\n评分标准\n5个空，每个3分；\n第4个空中，对了前半部分，给2分；移位正确给1分；\n第5个空中，每一个部分1分&lt;/p&gt;','','a:0:{}',1427352396,1,'',0,0,0),(1144,4,'&lt;p&gt;在管道通信机制中,用信号量描述读进程和写进程访问管道文件的过程,假设管\n道文件大小为10KB.&lt;/p&gt;',0,'','','',0,'&lt;p&gt;UNIX系统中,利用一个打开的共享文件来连接两个相互通信的进程,这个共享文 件叫管道.作为管道输入的发送进程,以字符流的形式将信息送入管道,而作为管道 输出的接收进程,从管道中获取信息.管道通信机制要提供三方面的协调能力:(1)互 斥.当一个进程对管道进行读/写操作时,另一个进程必须等待.(2) 同步.当写进程 把数据写入管道后便去睡眠等待,直到输出进程取走数据后唤醒它.若一次写入的数据 超过缓冲区剩余空间的大小,当缓冲区满时,写进程必须阻塞,并唤醒读进程。(3)对 方是否存在.只有确定对方存在时,才能够进行通信.本题只需要考虑互斥,同步问 题。由于只有一对进程访问管道,因此不需要设置互斥信号量,只要设置两个同步信号 量empty,full.分别表示管道可写和可读.&lt;/p&gt;\n\n&lt;pre&gt;&lt;code&gt;\nbegin\npipe:array[09] of kilobytes;\nts=10,length,in=0,out=0:integer;\nempty,full:semaphore=1,0;\ncobegin\nprocess PipeWriter\n  begin\n  repeat\n  产生数据;\n  p(empty);\n  length = data length; \n  while(length&gt;0 and ts&gt;0) \n  begin\n   pipe[in] = data of 1KB;\n   in = (in+1) mod n;\n   ts = ts-1;\n   length = length - 1;\n  end\n  v(full);\n  end\nprocess Consumer\n  begin\n  repeat;\n  p(full); \n  从缓冲区取出一件物品; \n  out = (out+1) mod n; \n  ts = ts +1; \n  v(empty);\n  end \ncoend \nend\n&lt;/code&gt;&lt;/pre&gt;','17','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"2\";s:5:\"knows\";s:24:\"实验环境准备实验\";}}',1427352887,1,'',0,0,0),(1145,4,'&lt;p&gt;桌上有一空盘,允许存放一只水果。爸爸可向盘中放苹果,也可向盘中放桔子,儿 子专等吃盘中的桔子,女儿专等吃盘中的苹果。规定当盘空时一次只能放一只水果供 吃者取用,请用P、V原语实现爸爸、儿子、女儿三个并发进程的同步&lt;/p&gt;',0,'','','',0,'&lt;p&gt;在本题中,爸爸、儿子、女儿共用一个盘子,盘中一次只能放一个水果。当盘子为 空时,爸爸可将一个水果放入果盘中。若放入果盘中的是桔子,则允许儿子吃,女儿必 须等待;若放入果盘中的是苹果,则允许女儿吃,儿子必须等待。本题实际上是生产 者-消费者问题的一种变形。这里,生产者放入缓冲区的产品有两类,消费者也有两类, 每类消费者只消费其中固定的一类产品。\n&lt;/p&gt;\n\n&lt;p&gt;在本题中,应设置三个信号量S、So、Sa,信号量S表示盘子是否为空,其初值为l; 信号量So表示盘中是否有桔子,其初值为0;信号量Sa表示盘中是否有苹果,其初值为0。 同步描述如下:&lt;/p&gt;\n\n&lt;pre&gt;&lt;code&gt;\nS=1; Sa=0; So=0;\ncobegin\n Procedure father;\n Procedure son;\n Procedure daughter;\ncoend\nProcedure father:\n  begin \n  while(TRUE) \n  begin\n  P(S); \n  将水果放入盘中; \n  if(放入的是桔子) \n  V(So);\n  else\n  V(Sa);\n  end\n  end\nProcedure son:\n  begin\n  while(TRUE)\n  begin\n  P(So); \n  从盘中取出桔子; \n  V(S);\n  吃桔子;\n  end\n  end\nProcedure daughter:\n  begin\n  while(TRUE)\n  begin\n  P(Sa); \n  从盘中取出苹果; \n  V(S);\n  吃苹果;\n  end\nend\n&lt;/code&gt;&lt;/pre&gt;','17','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"2\";s:5:\"knows\";s:24:\"实验环境准备实验\";}}',1427352887,1,'',0,0,0),(1146,4,'在南开大学至天津大学间有一条弯曲的路,每次只允许一辆自行车通过,但中间 有小的安全岛M(同时允许两辆车),可供两辆车在已进入两端小车错车,设计算法并使用P,V实现。',0,'','','',0,'&lt;p&gt;由于安全岛M仅仅允许两辆车停留,本应该作为临界资源而要设置信号量, 但根据 题意,任意时刻进入安全岛的车不会超过两辆(两个方向最多各有一辆), 因此,不需要 为M设置信号量,在路口s和路口t都需要设置信号量,以控制来自两个方向的车对路口资 源的争夺.这两个信号量的初值都是1.此外,由于从s到t的一段路只允许一辆车通过,所 以还需要设置另外的信号量用于控制,由于M的存在,可以为两端的小路分别设置一个互 斥信号量.&lt;/p&gt;\n\n&lt;pre&gt;&lt;code&gt;\nvar T2N, N2T,L,M,K:semaphore;\nT2N:=1;\nN2T:=1;\nL:=1;\nK:=1;\nM:=2;\ncobegin\n  Procedure Bike T2N\n  begin\n    p(T2N);\n    p(L);\n      go T to L;\n    p(M);\n      go into  M;\n    V(L);\n    P(k);\n     go K to s;\n    V(M);\n    V(k);\n    V(T2N);\n  end\n  Procedure Bike N2T\n  begin\n    P(N2T);\n    p(k);\n     go v to k;\n    p(M);\n      go into M;\n    V(k);\n    P(L);\n      go L to T;\n    V(M);\n    V(L);\n    V(N2T); \n  end\ncoend\n&lt;/code&gt;&lt;/pre&gt;','17','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"2\";s:5:\"knows\";s:24:\"实验环境准备实验\";}}',1427352887,1,'',0,0,0),(1147,4,'&lt;p&gt;在一个盒子里,混装了数量相等的黑白围棋子·现在用自动分拣系统把黑子、白 子分开,设分拣系统有二个进程P1 和P2 ,其中P1 拣白子;P2 拣黑子。规定每个进程 每次拣一子;当一个进程在拣时,不允许另一个进程去拣;当一个进程拣了一子时,必 须让另一个进程去拣.试写出两进程P1 和P2 能并发正确执行的程序。&lt;/p&gt;',0,'','','',0,'&lt;p&gt;大家熟悉了生产-消费问题(PC),这个问题很简单。题目较为新颖,但是本质非常 简单即:生产-消费问题的简化或者说是两个进程的简单同步问题。答案如下:&lt;/p&gt;\n\n&lt;p&gt;设信号量s1 和s2 分别表示可拣白子和黑子; 不失一般性,若令先拣白子。&lt;/p&gt;\n\n&lt;pre&gt;&lt;code&gt;\nvar S1 , S2 : semaphore;\nS1 : = l; S2 :=0;\ncobegin\n  process P1           Process P2\n   begin                begin\n   repeat               repeat\n   P(S1);               P(S2);\n   pick The white;      pick the black;\n   V(S2);               V(S1);\n  until false ;         until false;\n  end                   end\ncoend\n&lt;/code&gt;&lt;/pre&gt;','17','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"2\";s:5:\"knows\";s:24:\"实验环境准备实验\";}}',1427352887,1,'',0,0,0),(1148,4,'&lt;p&gt;设公共汽车上,司机和售票员的活动分别如下:司机的活动:启动车辆:正常行 车;到站停车。售票员的活动:关车门;售票;开车门。在汽车不断地到站、停车、行驶过程中,这两个活动有什么同步关系?用信号量和P 、V 操作实现它们的同步&lt;/p&gt;',0,'','','',0,'&lt;p&gt;在汽车行驶过程中,司机活动与售票员活动之间的同步关系为:售票员关车门后, 向司机发开车信号,司机接到开车信号后启动车辆,在汽车正常行驶过程中售票员售 票,到站时司机停车,售票员在车停后开门让乘客上下车。因此,司机启动车辆的动作 必须与售票员关车门的动作取得同步;售票员开车门的动作也必须与司机停车取得同 步。应设置两个信号量:S1 、S2 ;&lt;/p&gt;\n\n&lt;p&gt;S1表示是否允许司机启动汽车(其初值为0 )&lt;/p&gt;\n&lt;p&gt; S2表示是否允许售票员开门(其初值为0 )&lt;/p&gt;\n\n&lt;pre&gt;&lt;code&gt;\nvar S1,S2 : semaphore ; \n  S1=0;S2=0;\ncobegin\nProcedure driver             Procedure Conductor\nbegin                        begin\n while  TRUE                  while TRUE\n begin                        begin\n   P(S1);                       关车门;\n   Start;                       V(s1);\n   Driving;                     售票;\n   Stop;                        P(s2);\n   V(S2);                       开车门;\n end                            上下乘客;\nend                           end\ncoend\n&lt;/code&gt;&lt;/pre&gt;','17','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"2\";s:5:\"knows\";s:24:\"实验环境准备实验\";}}',1427352887,1,'',0,0,0),(1149,4,'&lt;p&gt;某寺庙,有小和尚、老和尚若干.庙内有一水缸,由小和尚提水入缸,供老和尚 饮用。水缸可容纳10桶水,每次入水、取水仅为1桶,不可同时进行。水取自同一井中, 水井径窄,每次只能容纳一个水桶取水。设水桶个数为3个,试用信号灯和PV操作给出 老和尚和小和尚的活动。&lt;/p&gt;',0,'','','',0,'&lt;p&gt;从井中取水并放入水缸是一个连续的动作可以视为一个进程,从缸中取水为另一 个进程。\n设水井和水缸为临界资源,引入mutex1,mutex2;三个水桶无论从井中取水还是放 入水缸中都一次一个,应该给他们一个信号量count,抢不到水桶的进程只好为等待, 水缸满了时,不可以再放水了。设empty控制入水量,水缸空了时,不可取水设full。&lt;/p&gt;\n\n&lt;pre&gt;&lt;code&gt;\nvar mutex1,mutex2,empty,full,count:semaphore;\nmutex1:=mutex2:=1;\nempty:=10;\nfull:=0;\ncount:=3;\ncobegin\n  Procedure Fetch_Water\n    begin\n     while true\n      p(empty);\n      P(count);\n      P(mutex1);\n       Get Water;\n      v(mutex1);\n      P(mutex2);\n      pure water into the jar;\n      v(mutex2);\n      v(count);\n      v(full); \n    end\ncoend\nProcedure Drink_Water\n begin\n   while true\n    p(full);\n    p(count);\n    p(mutex2);\n      Get water and\n      Drink water;\n    p(mutex2);\n    v(empty);\n    v(count);\nend\n&lt;/code&gt;&lt;/pre&gt;','17','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"2\";s:5:\"knows\";s:24:\"实验环境准备实验\";}}',1427352887,1,'',0,0,0),(1150,4,'&lt;p&gt;一座小桥(最多只能承重两个人)横跨南北两岸,任意时刻同一方向只允许一人过 桥,南侧桥段和北侧桥段较窄只能通过一人,桥中央一处宽敞,允许两个人通过或歇 息。试用信号灯和PV操作写出南、北两岸过桥的同步算法。&lt;/p&gt;',0,'','','',0,'&lt;p&gt;桥上可能没有人,也可能有一人,也可能有两人。&lt;/p&gt;\n\n&lt;p&gt;两人同时过桥&lt;/p&gt;\n&lt;p&gt;两人都到中间&lt;/p&gt;\n&lt;p&gt;南(北)来者到北(南)段&lt;/p&gt;\n\nnp&gt;共需要三个信号量,load用来控制桥上人数,初值为2,表示桥上最多有2人;north用 来控制北段桥的使用,初值为1,用于对北段桥互斥;south用来控制南段桥的使用,初 值为1,用于对南段桥互斥。&lt;/p&gt;\n\n&lt;pre&gt;&lt;code&gt;\nvar load,north,south:semaphore;\nload=2;\nnorth=1;\nsouth=1;\nGO_South()\nP(load);\nP(north);\n过北段桥;\n到桥中间; \nV(north); \nP(south);\n过南段桥;\n到达南岸; \nV(south); \nV(load);\nGO_North()\nP(load);\nP(south); \n过南段桥;\n到桥中间\nV(south);\nP(north);\n过北段桥;\n到达北岸\nV(north);\nV(load);\n&lt;/code&gt;&lt;/pre&gt;','17','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"2\";s:5:\"knows\";s:24:\"实验环境准备实验\";}}',1427352887,1,'',0,0,0),(1151,4,'下面是ucore中用于按需分页处理过程的内核代码。请补全其中所缺的代码，以正确完成按需分页过程.\n\n&lt;pre&gt;&lt;code&gt;\nkern/trap/trap.h\n---------------------------------\n...\nstruct trapframe {\n    struct pushregs tf_regs;\n    uint16_t tf_es;\n    uint16_t tf_padding1;\n    uint16_t tf_ds;\n    uint16_t tf_padding2;\n    uint32_t tf_trapno;\n    /* below here defined by x86 hardware */\n    uint32_t tf_err;\n    uintptr_t tf_eip;\n    uint16_t tf_cs;\n    uint16_t tf_padding3;\n    uint32_t tf_eflags;\n    // below here only when crossing rings, such as from user to kernel \n    uintptr_t tf_esp;\n    uint16_t tf_ss;\n    uint16_t tf_padding4;\n} __attribute__((packed));\n...\n---------------------------------\n\nkern/trap/trap.c\n---------------------------------\n...\nstatic int\npgfault_handler(struct trapframe *tf) {\n    extern struct mm_struct *check_mm_struct;\n\n}\nprint_pgfault(tf);\n    if (check_mm_struct != NULL) {\n        return do_pgfault(check_mm_struct, tf-&gt;tf_err, rcr2());\n    }\n    panic(&quot;unhandled page fault.\\n&quot;);\n}\n\nstatic void\ntrap_dispatch(struct trapframe *tf) {\nchar c;\nint ret;\nswitch ( --YOUR CODE 1-- ) {\n     .YOUR..\ncase T_PGFLT:\nif ( --YOUR CODE 2-- ) != 0) {\nprint_trapframe(trapf);\nif (current == NULL) {\npanic(&quot;handle pgfault failed. %e\\n&quot;,              ret);\n}\nelse { ... }\n}\nbreak;\n  ...\n}\n\nvoid\ntrap(struct trapframe *tf) {\n    // dispatch based on what type of trap occurred\n    trap_dispatch(tf);\n}\n...\n// do_pgfault - interrupt handler to process the page fault execption\nint\ndo_pgfault(struct mm_struct *mm, uint32_t error_code, uintptr_t addr) {\n    int ret = -E_INVAL;\n    struct vma_struct *vma = find_vma(mm, addr);\n    if (vma == NULL || vma-&gt;vm_start &gt; addr) {\n        goto failed;\n    }\n\n    switch (error_code &amp; 3) {\n    default:\n            /* default is 3: write, present */\n    case 2: /* write, not present */\n        if (!(vma-&gt;vm_flags &amp; VM_WRITE)) {\n            goto failed;\n        }\n        break;\n    case 1: /* read, present */\n        goto failed;\n    case 0: /* read, not present */\n        if (!(vma-&gt;vm_flags &amp; (VM_READ | VM_EXEC))) {\n            goto failed;\n        }\n    }\n\n    uint32_t perm = PTE_U;\n    if (vma-&gt;vm_flags &amp; VM_WRITE) {\n        perm |= PTE_W;\n    }\n    addr = ROUNDDOWN(addr, PGSIZE);\n\n    ret = -E_NO_MEM;\n\n    if (pgdir_alloc_page(mm-&gt;pgdir, addr, perm) == 0) {\n        goto failed;\n    }\n    ret = 0;\n\nfailed:\n    return ret;\n}\n...\n---------------------------------\n\nPmm.h\n---------------------------------\n...\n//ppn is physical page number\nstatic inline ppn_t\npage2ppn(struct Page *page) {\n    return --YOUR CODE 3--;\n}\n//pa is physical address\nstatic inline uintptr_t\npage2pa(struct Page *page) {\n    return --YOUR CODE 4--;\n}\n...\n---------------------------------\npmm.c\n---------------------------------\n...\n// virtual address of physicall page array\nstruct Page *pages;\n// amount of physical memory (in pages)\nsize_t npage = 0;\n// virtual address of boot-time page directory\npde_t *boot_pgdir = NULL;\n……\n// pgdir_alloc_page - call alloc_page &amp; page_insert functions to \n//                  - allocate a page size memory &amp; setup an addr map\n//                  - pa&lt;-&gt;la with linear address la and the PDT pgdir\nstruct Page *\npgdir_alloc_page(pde_t *pgdir, uintptr_t la, uint32_t perm) {\n    struct Page *page = alloc_page();\n    if (page != NULL) {\n        if (page_insert(pgdir, page, la, perm) != 0) {\n            free_page(page);\n            return NULL;\n        }\n    }\n    return page;\n}\n...\n\n//page_insert - build the map of phy addr of an Page with the linear addr la\n// paramemters:\n//  pgdir: the kernel virtual base address of PDT\n//  page:  the Page which need to map\n//  la:    the linear address need to map\n//  perm:  the permission of this Page which is setted in related pte\n// return value: always 0\n//note: PT is changed, so the TLB need to be invalidate \nint\npage_insert(pde_t *pgdir, struct Page *page, uintptr_t la, uint32_t perm) {\n    pte_t *ptep = get_pte(pgdir, la, 1);\n    if (ptep == NULL) {\n        return -E_NO_MEM;\n    }\n    page_ref_inc(page);\n    if (*ptep &amp; PTE_P) {\n        struct Page *p = pte2page(*ptep);\n        if (p == page) {\n            page_ref_dec(page);\n        }\n        else {\n            page_remove_pte(pgdir, la, ptep);\n        }\n    }\n    *ptep = --YOUR CODE 5--\n    tlb_invalidate(pgdir, la);\n    return 0;\n}\n---------------------------------\n&lt;/code&gt;&lt;/pre&gt;',0,'','','',0,'&lt;p&gt;Code1：  tf-&gt;tf_trapno\nCode 2:   ret = pgfault_handler(tf)\nCode 3:   page - pages;\nCode 4:   page2ppn(page) &lt;&lt; PGSHIFT\nCode 5:   page2pa(page) | PTE_P | perm;\n---------------------------------\n评分标准\n5个空，每个3分；\n第4个空中，对了前半部分，给2分；移位正确给1分；\n第5个空中，每一个部分1分&lt;/p&gt;','','a:0:{}',1427352887,1,'',0,0,0),(1152,4,'&lt;p&gt;3.说明五状态进程模型的状态定义和转换\n一、(16分) 请说明五状态进程模型中的状态和状态含义，并说明哪些状态会发生转换以及转换的原因。\n&lt;/p&gt;',0,'','','',0,'','11','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"1\";s:5:\"knows\";s:18:\"操作系统概述\";}}',1427352887,1,'',0,0,0),(1153,4,'&lt;p&gt;4.时钟置换算法\n一、(10分)  （1）试描述虚拟存储管理系统中的时钟置换算法（Clock Page Replacement）的工作原理；\n（2）假定在一个采用时钟置换算法的虚拟存储系统中某进程分配了4个物理页面，当进程按c, a, d, b, e, c, b, a, d, b, c, a, d的序列进行页面访问时，会出现多少次缺页？要求说明过程。如果需要，你可以补充假定条件。&lt;/p&gt;',0,'','','',0,'','10','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"1\";s:5:\"knows\";s:18:\"操作系统概述\";}}',1427352887,1,'',0,0,0),(1154,4,'&lt;p&gt;*5.地址变换和页表地址&lt;/p&gt;\n&lt;p&gt;一、(20分)（1）请使用图示来简要描述采用多级页面的虚拟存储系统中的地址变换过程。\n&lt;/p&gt;&lt;p&gt;（2）假定在一个32位计算机系统中，采用多级页表结构来实现虚拟存储管理，页面大小为4KB，每级页表大小为一个页面的大小，每个页表项占8字节。请问该计算机系统中要使用几级页表？各级各占多少位？对于虚拟地址0X87654321对应的各级页表号分别是多少？&lt;/p&gt;',0,'','','',0,'','10','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"1\";s:5:\"knows\";s:18:\"操作系统概述\";}}',1427352887,1,'',0,0,0),(1155,4,'&lt;p&gt;6.fork程序输出\n一、 (10分)给出程序fork.c的输出结果。注：（c1）getpid()和getppid()是两个系统调用，分别返回本进程标识和父进程标识。（2）你可以假定每次新进程创建时生成的进程标识是顺序加1得到的，该程序执行时创建的第一个进程的标识为1000。\n&lt;/p&gt;, \n\n&lt;pre&gt;&lt;code&gt;\nfork.c\n----------------------\n/* Includes */\n#include &lt;unistd.h&gt;     /* Symbolic Constants */\n#include &lt;sys/types.h&gt;  /* Primitive System Data Types */ \n#include &lt;errno.h&gt;      /* Errors */\n#include &lt;stdio.h&gt;      /* Input/Output */\n#include &lt;sys/wait.h&gt;   /* Wait for Process Termination */\n#include &lt;stdlib.h&gt;     /* General Utilities */\n \nint main()\n{\n    pid_t childpid; /* variable to store the child\'s pid */\n    int retval;     /* child process: user-provided return code */\n    int status;     /* parent process: child\'s exit status */\n    /* only 1 int variable is needed because each process would have its\n       own instance of the variable\n       here, 2 int variables are used for clarity */\n    /* now create new process */\n    childpid = fork();\n    \n    if (childpid &gt;= 0) /* fork succeeded */\n    {\n        if (childpid == 0) /* fork() returns 0 to the child process */\n        {\n            printf(&quot;CHILD: I am the child process!\\n&quot;);\n            printf(&quot;CHILD: Here\'s my PID: %d\\n&quot;, getpid());\n            printf(&quot;CHILD: My parent\'s PID is: %d\\n&quot;, getppid());\n            printf(&quot;CHILD: The value of my copy of childpid is: %d\\n&quot;, childpid);\n            printf(&quot;CHILD: Sleeping for 1 second...\\n&quot;);\n            sleep(1); /* sleep for 1 second */\n            printf(&quot;CHILD: Enter an exit value (0 to 255): &quot;);\n            scanf(&quot; %d&quot;, &amp;retval);\n            printf(&quot;CHILD: Goodbye!\\n&quot;);    \n            exit(retval); /* child exits with user-provided return code */\n        }\n        else /* fork() returns new pid to the parent process */\n        {\n            printf(&quot;PARENT: I am the parent process!\\n&quot;);\n\n            printf(&quot;PARENT: Here\'s my PID: %d\\n&quot;, getpid());\n            printf(&quot;PARENT: The value of my copy of childpid is %d\\n&quot;, childpid);\n            printf(&quot;PARENT: I will now wait for my child to exit.\\n&quot;);\n            wait(&amp;status); /* wait for child to exit, and store its status */\n            printf(&quot;PARENT: Child\'s exit code is: %d\\n&quot;, WEXITSTATUS(status));\n            printf(&quot;PARENT: Goodbye!\\n&quot;);             \n            exit(0);  /* parent exits */       \n        }\n    }\n    else /* fork returns -1 on failure */\n    {\n        perror(&quot;fork&quot;); /* display error message */\n        exit(0); \n    }\n}&lt;/code&gt;&lt;/pre&gt;',0,'','','',0,'','13','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"2\";s:5:\"knows\";s:24:\"实验环境准备实验\";}}',1427352887,1,'',0,0,0),(1156,4,'&lt;h&gt;7 内存计算题&lt;/h&gt;\n&lt;p&gt;一、 (12分) 请求分页管理系统中，假定页表内容下表所示：\n&lt;table&gt;\n&lt;tr&gt;\n  &lt;th&gt;页号&lt;/th&gt;\n  &lt;th&gt;页框（Page Frame）号&lt;/th&gt;\n  &lt;th&gt;有效位（存在位）&lt;/th&gt;\n&lt;/tr&gt;\n&lt;tr&gt;\n  &lt;td&gt;0&lt;/td&gt;\n  &lt;td&gt;101H&lt;/td&gt;\n  &lt;td&gt;1&lt;/td&gt;\n&lt;/tr&gt;\n&lt;tr&gt;\n  &lt;td&gt;1&lt;/td&gt;\n  &lt;td&gt;N/A&lt;/td&gt;\n  &lt;td&gt;0&lt;/td&gt;\n&lt;/tr&gt;\n&lt;tr&gt;\n  &lt;td&gt;2&lt;/td&gt;\n  &lt;td&gt;254H&lt;/td&gt;\n  &lt;td&gt;1&lt;/td&gt;\n&lt;/tr&gt;\n&lt;p&gt;\n页面大小为4KB，一次内存的访问时间是100ns，一次快表（TLB）的访问时间是10ns，处理一次缺页的平均时间是10^8ns（已包含更新TLB和页表的时间），进程的驻留集大小固定为2，采用最近最少使用置换算法（LRU）和局部淘汰策略。假设：TLB初始为空；地址转换时先访问TLB，若TLB未命中，再访问页表（忽略访问页表之后的TLB更新时间）；有效位为0表示页面不在内存，会产生缺页中断，缺页中断处理后，返回到产生缺页中断的指令处重新执行。设有虚地址访问序列2362H、1565H、25A5H，请问：&lt;/p&gt;\n&lt;p&gt;\n（1）依次访问上述三个虚地址，各需多少时间？给出计算过程。\n（2）基于上述访问序列，虚地址1565H的物理地址是多少？请说明理由\n&lt;/p&gt;',0,'','','',0,'','10','a:1:{i:0;a:2:{s:7:\"knowsid\";s:1:\"3\";s:5:\"knows\";s:30:\"中断、异常与系统调用\";}}',1427352887,1,'',0,0,0),(1157,4,'&lt;h&gt;系统调用访问过程&lt;/h&gt;\n&lt;p&gt;\n第一题：\n一、（2362H2分）下面是与read()系统调用实现相关源代码。请补全其中所缺的代码，以正确完成从用户态函数read()到内核态函数sysfile_read()的参数传递和返回过程。提示：每处需要补全的代码只需要一行，一共有10个空要填。&lt;/p&gt;\n&lt;pre&gt;&lt;code&gt;\nuser/libs/file.c\n---------------------------------\n...\nint\nread(int fd, void *base, size_t len) {\n    ...(1)...\n}\n...\n---------------------------------\nuser/libs/syscall.c\n---------------------------------\n...\n#define MAX_ARGS\nstatic inline int\nsyscall(int num, ...) {\n          int ret;\n          va_list ap;\n         va_start(ap, num);\n         uint32_t a[MAX_ARGS];\n         int i;\n         for (i = 0; i &lt; MAX_ARGS; i ++) {\n                   a[i] = va_arg(ap, uint32_t);\n         }\n         va_end(ap);\n \n         asm volatile (\n                            &quot;int %1;&quot;\n                            : &quot;=a&quot; (ret)\n                            : &quot;i&quot; (T_SYSCALL),\n                              &quot;a&quot; (num),\n                              &quot;d&quot; (a[0]),\n                              &quot;c&quot; (a[1]),\n                              &quot;b&quot; (a[2]),\n                              &quot;D&quot; (a[3]),\n                              &quot;S&quot; (a[4])\n                            : &quot;cc&quot;, &quot;memory&quot;);\n         return ret;\n}\n...\nint\nsys_read(int fd, void *base, size_t len) {\n         ...(2)...\n}\n...\n---------------------------------\nlibs/stdarg.h\n---------------------------------\n...\ntypedef char * va_list;\n \n#define __va_size(type)                                                              \\\n         ((sizeof(type) + (sizeof(long) - 1)) / sizeof(long) * sizeof(long))\n \n#define va_start(ap, last)                                                    \\\n         ((ap) = (va_list)&amp;(last) + __va_size(last))\n \n#define va_arg(ap, type)                                                    \\\n         (*(type *)((ap) += __va_size(type), (ap) - __va_size(type)))\n \n#define va_end(ap)             ((void)0)\n...\n---------------------------------\n \nlibs/unistd.h\n---------------------------------\n...\n#define T_SYSCALL                           0x80\n \n/* syscall number */\n...\n#define SYS_read                       102\n#define SYS_write                      103\n...\n---------------------------------\nkern/syscall/syscall.c\n---------------------------------\n...\nstruct trapframe {\n         struct pushregs tf_regs;\n         uint16_t tf_es;\n         uint16_t tf_padding1;\n         uint16_t tf_ds;\n         uint16_t tf_padding2;\n         uint32_t tf_trapno;\n         /* below here defined by x86 hardware */\n         uint32_t tf_err;\n         uintptr_t tf_eip;\n         uint16_t tf_cs;\n         uint16_t tf_padding3;\n         uint32_t tf_eflags;\n         /* below here only when crossing rings, such as from user to kernel */\n         uintptr_t tf_esp;\n         uint16_t tf_ss;\n         uint16_t tf_padding4;\n};\n...\n---------------------------------\nkern/trap/trap.c\n---------------------------------\n...\nstatic void\ntrap_dispatch(struct trapframe *tf) {\n         char c;\n \n         int ret;\n \n         switch (...(3)...) {\n         case T_DEBUG:\n         case T_BRKPT:\n                   debug_monitor(tf);\n                   break;\n         case T_PGFLT:\n                   if ((ret = pgfault_handler(tf)) != 0) {\n                            print_trapframe(tf);\n                            if (current == NULL) {\n                                     panic(&quot;handle pgfault failed. %e\\n&quot;, ret);\n                            }\n                            else {\n                                     if (trap_in_kernel(tf)) {\n                                               panic(&quot;handle pgfault failed in kernel mode. %e\\n&quot;, ret);\n                                     }\n                                     cprintf(&quot;killed by kernel.\\n&quot;);\n                                     do_exit(-E_KILLED);\n                            }\n                   }\n                   break;\n         case T_SYSCALL:\n                   ...(4)...\n                   break;\n         case IRQ_OFFSET + IRQ_TIMER:\n                   ticks ++;\n                   assert(current != NULL);\n                   run_timer_list();\n                   break;\n         case IRQ_OFFSET + IRQ_COM1:\n         case IRQ_OFFSET + IRQ_KBD:\n                  if ((c = cons_getc()) == 13) {\n                            debug_monitor(tf);\n                   }\n                   else {\n                            extern void dev_stdin_write(char c);\n                            dev_stdin_write(c);\n                   }\n                   break;\n         case IRQ_OFFSET + IRQ_IDE1:\n         case IRQ_OFFSET + IRQ_IDE2:\n                   /* do nothing */\n                   break;\n         default:\n                   print_trapfr